$(document).ready(function(){

	
	
	
	//광고 bxslider
	$(document).ready(function(){
		$('.bxslider').bxSlider({
			mode: 'horizontal',
			moveSlides: 1,
			slideMargin: 0,
			infiniteLoop: true,
			slideWidth: 100,
			minSlides: 1,
			maxSlides: 1,
			speed: 800,
			controls:true
		});
	});
	
	
})